/* ----- Catppuccin Colours ----- */
:root {
    /* ----- rainbow ----- */
    /*                 H     S     L */
    --rosewater_hsl:  10,  56%,  91%;
    --flamingo_hsl:    0,  59%,  88%;
    --mauve_hsl:     279,  70%,  83%;
    --pink_hsl:      316,  72%,  86%;
    --maroon_hsl:    349,  60%,  77%;
    --red_hsl:       342,  79%,  75%;
    --peach_hsl:      24,  88%,  78%;
    --yellow_hsl:     41,  88%,  84%;
    --green_hsl:     128,  58%,  79%;
    --teal_hsl:      171,  53%,  81%;
    --blue_hsl:      207,  93%,  79%;
    --sky_hsl:       189,  71%,  73%;
    --lavender_hsl:  238, 100%,  89%;
    /* ----- black & white ----- */
    /*                 H     S     L */
    --black0_hsl:    254,  25%,  10%;
    --black1_hsl:    249,  23%,  12%;
    --black2_hsl:    240,  21%,  15%;
    --black3_hsl:    249,  18%,  22%;
    --black4_hsl:    254,  12%,  36%;
    --gray0_hsl:     247,   8%,  46%;
    --gray1_hsl:     274,  11%,  59%;
    --gray2_hsl:     285,  10%,  75%;
    --white_hsl:     220,  38%,  89%;
    /* ----- variables ----- */
    --red:           hsl(var(--red_hsl));
    --yellow:        hsl(var(--yellow_hsl));
    --green:         hsl(var(--green_hsl));
    --blue:          hsl(var(--blue_hsl));
    --text_color:    hsl(var(--white_hsl));
	--text_color_alt:    hsl(var(--gray0_hsl));
    --background:    hsl(var(--black0_hsl));
    --h1:            hsl(var(--peach_hsl));
    --h2:            hsl(var(--teal_hsl));
    --h3:            hsl(var(--mauve_hsl));
    --h4:            hsl(var(--maroon_hsl));
    --h5:            hsl(var(--sky_hsl));
    --highlight1:    hsl(var(--red_hsl));
    --highlight2:    hsl(var(--green_hsl));
    --highlight3:    hsl(var(--lavender_hsl));
    --highlight4:    hsl(var(--pink_hsl));
    --extra:         hsl(var(--yellow_hsl));
    --listheader1:   hsl(var(--teal_hsl));
    --listheader2:   hsl(var(--sky_hsl));
    --listheader3:   hsl(var(--blue_hsl));
    --tablehead:     hsl(var(--red_hsl));
    --italics:       hsl(var(--green_hsl));
    --bold:          hsl(var(--maroon_hsl));
    --hyperlink:     hsl(var(--sky_hsl));
    --table_border:  hsl(var(--gray0_hsl));
    --card1:         hsl(var(--black1_hsl));
    --card2:         hsl(var(--black1_hsl));
    --card1_alt:     hsl(var(--black2_hsl));
    --card2_alt:     hsl(var(--black2_hsl));
    --deck_tag:      hsl(var(--green_hsl));
    --tag_tag:       hsl(var(--peach_hsl));
    --tag:           hsl(var(--black0_hsl));
    --footer:        hsl(var(--black0_hsl));
    --cloze:         hsl(var(--yellow_hsl));
    --separators:    hsl(var(--black4_hsl));
    --footer_color:  hsl(var(--white_hsl));

}
/* ----- Customization ----- */

    @import url('https://fonts.googleapis.com/css2?family=JetBrains+Mono&display=swap');
    :root {
        --font_size: 20px;
        --max_width_holder: 100%;
        --textalign: center;
        --spacing_cards: 10px 10px;
        --footer_tag_font: 80%;
        --footer_tag_padding: 5px
        --mobile_font: 15px;
        --table_min_width: 70%;
        --header_margin: 0;
        --border_width: 2px;
        --image_radius: 10px;
    }
.card, .card.nightMode, .mobile.card, .mobile.card.nightMode {
    text-align: var(--textalign);
    font-size: var(--font_size);
    color: var(--text_color);
    background-color: var(--background);
}
#FirstCard, #SecondCard, #Footer {
    padding: var(--spacing_cards);
}
#FirstCard {
    background-color: var(--card1);
}
#SecondCard {
    background-color: var(--card2);
    border-top-color: var(--separators);
    border-top-width: 1px;
    border-top-style: dashed;
}
#Holder {
    border-radius: 10px;
    overflow: scroll;
    max-width: var(--max_width_holder);
    margin: 0 auto;
    margin-top: 5px;
    margin-bottom: 4px;
    border: 1px solid;
    text-align: var(--textalign);
    border-color: var(--separators);
	background-color: var(--card1);
}
#Tags,  #Footer {
  padding: 4px;
  padding-left: 15px;
    font-size: var(--footer_tag_font);
}
#Tags {
    text-align: center;
    background-color: var(--tag);
	color: var(--tag_tag);
    border-bottom-color: var(--separators);
    border-bottom-width: 1px;
    border-bottom-style: dashed;
}
#Footer {
 text-align: center;
    background-color: var(--footer);
    border-top-color: var(--separators);
    color: var(--text_color_alt);
    border-top-width: 1px;
    border-top-style: dashed;
}
#FirstCard::-webkit-scrollbar, #SecondCard::-webkit-scrollbar,#Holder::-webkit-scrollbar {
  display: none;
}
#FirstCard, #SecondCard,#Holder {
  -ms-overflow-style: none;  /* IE and Edge */
  scrollbar-width: none;  /* Firefox */
}
.mobile.card {
    font-size: var(--mobile_font);
}
table {
    border-collapse: collapse;
    min-width: var(--table_min_width);
    position: relative;
    box-align: center;
    margin-left: auto;
    margin-right: auto;
}
.cloze, .cloze b, .cloze u, .cloze i {
    font-weight: bold;
}
#Extra, #Extra i, .mobile #Extra, .mobile #Extra i {
    font-style: italic;
    color: var(--extra)
}
img {
    display: block;
    max-width: 100%;
    max-height: none;
    margin: 10px auto 10px auto;
    border-radius: var(--image_radius);
}
h1, h2, h3, h4, h5 {
    margin: var(--header_margin);
}
td, th {
    border: var(--border_width) solid;
    text-align: center;
    border-color: var(--separators);
}
li {
    list-style: inside;
}
hr {
    border: 1px solid;
    border-radius: 2px;
    width: 70%;
    border-color: var(--separators);
}
a {
    text-decoration: none;
    font-style: normal;
    color: var(--hyperlink) !important;
}
hl {
    color: var(--highlight1);
}
hl2 {
    color: var(--highlight2);
}
hl3 {
    color: var(--highlight3);
}
hl4 {
    color: var(--highlight4);
}
h1 {
    color: var(--h1);
}
h2 {
    color: var(--h2);
}
h3 {
    color: var(--h3);
}
h4 {
    color: var(--h4);
}
h5 {
    color: var(--h5);
}
li b, ol b { 
    color: var(--listheader1); 
}
li ul li b, li ol li b { 
    color: var(--listheader2);
 }
li ul li ul li b, li ol li ol li b { 
    color: var(--listheader3);
 }
th {
    color: var(--tablehead); 
}
#FirstCard th {
    background-color: var(--card1);
}
#SecondCard th {
    background-color: var(--card2);
}
b {
    color: var(--bold); 
}
em, i {
    color: var(--italics); 
}
.mobile {
    overflow:scroll imo !important;
}
.cloze, .cloze b, .cloze u, .cloze i, .cloze hl, .cloze hl2, .cloze hl3, .cloze em, .cloze td,  .cloze table,  .cloze tr {
    color: var(--cloze) !important; 
}
#FirstCard table tr:nth-of-type(odd) {
    background-color: var(--card1_alt);
  }
#SecondCard table tr:nth-of-type(odd) {
    background-color: var(--card2_alt);
  }

