}
hl2 {
    color: var(--highlight2);
}
hl3 {
    color: var(--highlight3);
}
hl4 {
    color: var(--highlight4);
}
h1 {
    color: var(--h1);
}
h2 {
    color: var(--h2);
}
h3 {
    color: var(--h3);
}
h4 {
    color: var(--h4);
}
h5 {
    color: var(--h5);
}
li b, ol b { 
    color: var(--listheader1); 
}
li ul li b, li ol li b { 
    color: var(--listheader2);
 }
li ul li ul li b, li ol li ol li b { 
    color: var(--listheader3);
 }
